Machine.ScriptBlock:
create function TimeReport() returns @TimeStamp table(Date varchar(100),TimeIn varchar(100), TimeOut varchar(100)) as begin /*Time In Data*/ declare @TimeIn TABLE (RowNum int, EventDate datetime, TypContentID int, ExternalID varchar(50)); insert into @TimeIn select ROW_NUMBER() over(order by tic.EventDate) RowNum,tic.EventDate,tic.TypeContentID, tic.ExternalID from Calendar tic where tic.TypeContentID = (select id from TypeContent where ExternalID = 'TimeStampIn')
/*Time Out Data*/ declare @TimeOut TABLE (RowNum int, EventDate datetime, TypContentID int, ExternalID varchar(50)); insert into @TimeOut select ROW_NUMBER() over(order by toc.EventDate) RowNum,toc.EventDate,toc.TypeContentID, toc.ExternalID from Calendar toc where toc.TypeContentID = (select id from TypeContent where ExternalID = 'TimeStampOut')
insert into @TimeStamp select convert(varchar(10), TimeIn.EventDate, 101) [Date], convert(varchar, TimeIn.EventDate, 20)[TimeIn], case when TimeOut.EventDate is null then (select '--:--:-- --') else convert(varchar, TimeOut.EventDate, 20) end [TimeOut] from @TimeIn TimeIn full outer join @TimeOut TimeOut on TimeIn.RowNum = TimeOut.RowNum and TimeIn.ExternalID = TimeOut.ExternalID return end


/*** Put value for new TypeContent column ***/
if exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_NAME = 'TypeContent') 
begin 
  update TypeContent
  set StartDate = GETDATE()
end


if exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_NAME = 'Calendar') 
begin 
  alter table Calendar alter column EventDate datetime NOT NULL;
end


if exists (select * from INFORMATION_SCHEMA.COLUMNS where TABLE_NAME = 'Calendar' and COLUMN_NAME in ('StartTime', 'EndTime'))
begin
  alter table Calendar drop column StartTime, EndTime;  
end


if exists (select * from INFORMATION_SCHEMA.TABLES where TABLE_NAME = 'TypeContent') 
begin 
  delete TypeContent where externalid in ('Token', 'MobileNumber', 'HomeAddress');
end
